package DAY2;

public class IfElseIfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int code=3;
		String color=null;
		if(code==1)
		{
			color="GREEN";
		}
		else if(code==2)
		{
			color="BLUE";
		}
		else if(code ==3)
		{
			color="RED";
		}
		else
		{
			color="BLACK";
		}
		System.out.println(color);
	}

}
